(22, 64, '2016-05-03', 'Operasi Katarak Mei 2016', '<p><span style="font-size:12px">Untuk Semua,</span></p>\r\n\r\n<p><span style="font-size:12px">Hari ini kita telah melakukan Operasi Katarak untuk 100 pasien dan dibantu oleh 4 orang Tim Operasi dari Tim Perdami. Se<span style="color:rgb(0, 0, 0); font-family:pt sans narrow,sans-serif">mua pasien akan melaksanakan tes pengujian mata oleh tim Perdamai, berkerja sama dengan tim medis di klinik kesehatan.&nbsp;</span></span></p>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:tahoma,verdana,arial">Pasien yang telah melakukan operasi perlu kembali ke dokter untuk pemeriksaan mata sehari setelah operasi. Pemerikasaan berikutnya biasanya setelah 1 minggu dan 1 bulan setelah operasi.&nbsp;</span></span></p>\r\n\r\n<p><span style="font-size:12px"><span style="color:rgb(0, 0, 0); font-family:pt sans narrow,sans-serif">Kami berterima kasih kepada Anda semua lagi atas dukungan Anda dan kami akan memberikan update tambahan minggu depan.</span></span></p>\r\n\r\n<p><span style="font-size:12px">Salam.</span></p>\r\n\r\n<p><span style="font-size:12px">Besi Pae</span></p>\r\n', '', '', '', ''),
(23, 97, '2016-08-29', 'Operasi Katarak dijadwalkan pada Oktober 2016', '<p>Operasi katarak bersama Perdami telah dijadwalkan akan dilaksanakan pada pertengahan Oktober 2016. Besi Pae saat ini tengah&nbsp;<span style="line-height:1.6">menghubungi pasien-pasien potensial untuk konsultasi dan pengujian mata.</span></p>\r\n', 'Cataract Operations Scheduled on October 2016', '<p><span style="color:rgb(0, 0, 0); font-family:verdana,arial,helvetica,sans-serif; font-size:12px">The cataract operations with Perdami has now been scheduled to the middle of October 2016. Besi Pae is now contacting potential patients for consultation and eye testing.</span></p>\r\n', '-', '-'),
(24, 97, '2016-09-16', 'Jadwal Keberangkatan Tim Dokter', '<p><span style="color:rgb(0, 0, 0); font-family:verdana,arial,helvetica,sans-serif; font-size:12px">Tim atas 15 dokter dan perawat akan berangkat menuju Soe pada tanggal 16 Oktober. Tiket telah dibeli dan akomodasi telah disiapkan. Yayasan Besi Pae akan menginformasikan kepada publik tanggal pelaksanaan operasi selanjutnya.&nbsp;</span></p>\r\n', 'Doctors Travel Schedule', '<p><span style="color:rgb(0, 0, 0); font-family:verdana,arial,helvetica,sans-serif; font-size:12px">A team of 15 doctors and nurses will travel to Soe on the 16</span><sup>th</sup><span style="color:rgb(0, 0, 0); font-family:verdana,arial,helvetica,sans-serif; font-size:12px">&nbsp; October. Tickets have been purchased and accommodation booked. Yayasan Besi Pae is informing the public of the dates for the next round of operations.</span></p>\r\n', 'Pada 16 Oktober, 15 dokter dan perawat dijadwalkan berangkat menuju Soe', 'A team of 15 doctors and nurses will travel to Soe on the 16th  October');
